mapper.select:
SELECT * 
FROM gsxy_user
WHERE userCode = #{userCode} AND userPassword = #{userPassword}

SELECT * 
FROM gsxy_user
WHERE userCode = #{userCode} AND userPassword = #{userPassword}

mapper.update:
UPDATE `gsxy_user`
userCode = #{userCode}, userName = #{userName}, userPhone = #{userPhone}, userPassword = #{userPassword}, idCard = #{idCard}, userAge = #{userAge}, userGender = #{userGender},
WHERE `userId` = #{userId}
